import { unlock } from "@/api/index";
import Cookies from "js-cookie";
export default {
  name: "Unlock",
  data: function data() {
    return {
      avatorLeft: "0px",
      inputLeft: "400px",
      password: "",
      check: null
    };
  },
  props: {
    showUnlock: {
      type: Boolean,
      default: false
    }
  },
  computed: {
    avatarPath: function avatarPath() {
      return localStorage.avatorImgPath;
    }
  },
  methods: {
    unlock: function unlock() {
      this.avatorLeft = "0px";
      this.inputLeft = "400px";
      this.password = "";
      Cookies.set("locking", "0");
      this.$emit("on-unlock");
    },
    handleClickAvator: function handleClickAvator() {
      this.avatorLeft = "-180px";
      this.inputLeft = "0px";
      this.$refs.inputEle.focus();
    },
    handleUnlock: function handleUnlock() {
      var _this = this;

      if (this.password == "") {
        this.$Message.error("请输入密码");
        return;
      } // 将用户输入的密码this.password与数据库用户密码对比


      var flag = false;
      unlock({
        password: this.password
      }).then(function (res) {
        if (res.success) {
          _this.unlock();
        }
      });
    },
    unlockMousedown: function unlockMousedown() {
      this.$refs.unlockBtn.className = "unlock-btn click-unlock-btn";
    },
    unlockMouseup: function unlockMouseup() {
      this.$refs.unlockBtn.className = "unlock-btn";
    }
  }
};
