import Cookies from "js-cookie";

var setLockBackSize = function setLockBackSize() {
  var x = document.body.clientWidth;
  var y = document.body.clientHeight;
  var r = Math.sqrt(x * x + y * y);
  return parseInt(r);
};

export default {
  name: "lockScreen",
  props: {
    value: {
      type: Boolean,
      default: false
    }
  },
  methods: {
