import { unlock } from "@/api/index";
export default {
  name: "circleLoading",
  data: function data() {
    return {
      loading: false,
      passCheckVisible: false,
      // 密码验证
      password: ""
    };
  },
  methods: {
    checkPass: function checkPass() {
      var _this = this;

      this.loading = true;
      unlock({
        password: this.password
      }).then(function (res) {
        _this.loading = false;

        if (res.success) {
          _this.passCheckVisible = false;

          _this.$emit("on-success", true);
        }
      });
    },
    show: function show() {
      this.password = "";
      this.passCheckVisible = true;
    }
  }
};
