import { icons } from "@/libs/icon";
export default {
  name: "iconChoose",
  props: {
    value: {
      type: String,
      default: ""
    },
    size: String,
    placeholder: {
      type: String,
      default: "输入图标名或选择图标"
    },
    disabled: {
      type: Boolean,
      default: false
    },
    readonly: {
      type: Boolean,
      default: false
    },
    maxlength: Number,
    icon: {
      type: String,
      default: "md-ionic"
    }
  },
  data: function data() {
    return {
      iconModalVisible: false,
      currentValue: this.value,
      iconData: [],
      key: "",
      tip: "输入英文关键词搜索，比如 success"
    };
  },
  methods: {
    init: function init() {
      var re = [];
      icons.forEach(function (e) {
        e.icons.forEach(function (item) {
          re.push(item);
        });
      });
      this.iconData = re;
    },
    handleInput: function handleInput() {
      var _this = this;

      if (this.key) {
        // 搜索
        var re = [];
        icons.forEach(function (e) {
          e.tags.forEach(function (item) {
            if (item.indexOf(_this.key) >= 0) {
              e.icons.forEach(function (r) {
                re.push(r);
              });
            }
          });
        });
        this.iconData = re;
      } else {
        this.init();
      }
    },
    handleFocus: function handleFocus() {
      if (!this.key) {
        this.tip = "";
      }
    },
    handleBlur: function handleBlur() {
      if (!this.key) {
        this.tip = "输入英文关键词搜索，比如 success";
      }
    },
    handleChange: function handleChange(v) {
      this.$emit("input", this.currentValue);
      this.$emit("on-change", this.currentValue);
    },
    setCurrentValue: function setCurrentValue(value) {
      if (value == this.currentValue) {
        return;
      }

      this.currentValue = value;
    },
    hanleChoose: function hanleChoose(v) {
      this.currentValue = v;
      this.$emit("input", this.currentValue);
      this.$emit("on-change", this.currentValue);
      this.iconModalVisible = false;
    }
  },
  watch: {
    value: function value(val) {
      this.setCurrentValue(val);
    }
  },
  created: function created() {
    this.init();
  }
};
