import { login, userInfo, initCaptcha, drawCodeImage } from "@/api/index";
import Cookies from "js-cookie";
import Header from "@/views/main-components/header";
import LangSwitch from "@/views/main-components/lang-switch";
import util from "@/libs/util.js";
export default {
  components: {
    LangSwitch: LangSwitch,
    Header: Header
  },
  data: function data() {
    return {
      captchaId: "",
      captchaImg: "",
      loadingCaptcha: true,
      error: false,
      saveLogin: true,
      getSms: "获取验证码",
      loading: false,
      sending: false,
      errorCode: "",
      form: {
        username: "",
        password: "",
        code: ""
      },
      rules: {
        username: [{
          required: true,
          message: "账号不能为空",
          trigger: "blur"
        }],
        password: [{
          required: true,
          message: "密码不能为空",
          trigger: "blur"
        }],
        imgCode: [{
          required: false,
          message: "验证码不能为空",
          trigger: "blur"
        }]
      }
    };
  },
  methods: {
    getCaptchaImg: function getCaptchaImg() {
      var _this = this;

      this.loadingCaptcha = true;
      initCaptcha().then(function (res) {
        _this.loadingCaptcha = false;

        if (res.success) {
          _this.captchaId = res.result;
          _this.captchaImg = drawCodeImage + _this.captchaId;
        }
      });
    },
    submitLogin: function submitLogin() {
      var _this2 = this;

      this.$refs.usernameLoginForm.validate(function (valid) {
        if (valid) {
          _this2.loading = true;
          login({
            username: _this2.form.username,
            password: _this2.form.password,
            code: _this2.form.imgCode,
            captchaId: _this2.captchaId,
            saveLogin: _this2.saveLogin
          }).then(function (res) {
            if (res.success) {
              console.log(res);

              _this2.setStore("accessToken", res.result);

              Cookies.set("lastLoginUser", _this2.form.username);
              Cookies.set(_this2.form.username, _this2.form.password, {
                expires: 7
              }); // 获取用户信息

              console.log(_this2.getStore("accessToken"));
              userInfo().then(function (res) {
                if (res.success) {
                  // 避免超过大小限制
                  delete res.result.permissions;
                  var roles = [];
                  res.result.roles.forEach(function (e) {
                    roles.push(e.name);
                  });

                  _this2.setStore("roles", roles);

                  _this2.setStore("saveLogin", _this2.saveLogin);

                  if (_this2.saveLogin) {
                    // 保存7天
                    Cookies.set("userInfo", JSON.stringify(res.result), {
                      expires: 7
                    });
                  } else {
                    Cookies.set("userInfo", JSON.stringify(res.result));
                  }

                  _this2.setStore("userInfo", res.result);

                  _this2.$store.commit("setAvatarPath", res.result.avatar); // 加载菜单


                  util.initRouter(_this2);

                  _this2.$router.push({
                    name: "home_index"
                  });
                } else {
                  _this2.loading = false;
                }
              });
            } else {
              _this2.loading = false; //this.getCaptchaImg();
            }
          });
        }
      });
    },
    getCookie: function getCookie() {
      this.form.username = Cookies.get("lastLoginUser");

      if (this.form.username) {
        this.form.password = Cookies.get(this.form.username);
      }
    }
  },
  mounted: function mounted() {
    //this.getCaptchaImg();
    this.getCookie();
  }
};
