import { sendMail } from "@/api/index";
export default {
  name: "sendMail",
  data: function data() {
    return {
      form: {
        userId: '-1',
        title: '',
        content: '',
        type: '1',
        reward: ''
      },
      succ: [],
      fail: [],
      disabledInput: true
    };
  },
  methods: {
    disableUserId: function disableUserId(label) {
      if (label == 1) {
        this.form.userId = '-1';
        this.disabledInput = true;
      } else {
        this.form.userId = '';
        this.disabledInput = false;
      }
    },
    onSubmit: function onSubmit() {
      var _this = this;

      sendMail(this.form).then(function (res) {
        if (res.status === 200) {
          console.log(res);
          var succCnt = 0;
          var failCnt = 0;

          if (res.data.succ) {
            _this.succ = res.data.succ;
            succCnt = _this.succ.length;
          }

          if (res.data.fail) {
            _this.fail = res.data.fail;
            failCnt = _this.fail.length;
          }

          _this.$Message.success('发送成功' + succCnt + "个,失败了" + failCnt + "个！");
        } else if (res.status === -1) {
          _this.$Message.error(res.data);
        }
      });
    }
  }
};
