import { getUserListData } from "@/api/index";
export default {
  name: "userChoose",
  props: {
    text: {
      type: String,
      default: "选择用户"
    },
    icon: {
      type: String,
      default: "md-person-add"
    }
  },
  data: function data() {
    var _this = this;

    return {
      height: 500,
      userLoading: true,
      userModalVisible: false,
      selectUsers: [],
      searchUserForm: {
        username: "",
        type: "",
        status: "",
        pageNumber: 1,
        // 当前页数
        pageSize: 10,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc" // 默认排序方式

      },
      userColumns: [{
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "用户名",
        key: "username",
        width: 145,
        sortable: true
      }, {
        title: "头像",
        key: "avatar",
        width: 80,
        align: "center",
        render: function render(h, params) {
          return h("Avatar", {
            props: {
              src: params.row.avatar
            }
          });
        }
      }, {
        title: "手机",
        key: "mobile",
        width: 115,
        sortable: true
      }, {
        title: "邮箱",
        key: "email",
        width: 180,
        sortable: true
      }, {
        title: "性别",
        key: "sex",
        width: 70,
        align: "center"
      }, {
        title: "用户类型",
        key: "type",
        align: "center",
        width: 100,
        render: function render(h, params) {
          var re = "";

          if (params.row.type == 1) {
            re = "管理员";
          } else if (params.row.type == 0) {
            re = "普通用户";
          }

          return h("div", re);
        }
      }, {
        title: "状态",
        key: "status",
        align: "center",
        width: 120,
        render: function render(h, params) {
          if (params.row.status == 0) {
            return h("div", [h("Badge", {
              props: {
                status: "success",
                text: "正常启用"
              }
            })]);
          } else if (params.row.status == -1) {
            return h("div", [h("Badge", {
              props: {
                status: "error",
                text: "禁用"
              }
            })]);
          }
        }
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        width: 150
      }, {
        title: "操作",
        key: "action",
        width: 130,
        align: "center",
        fixed: "right",
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "info",
              size: "small"
            },
            on: {
              click: function click() {
                _this.chooseUser(params.row);
              }
            }
          }, "添加该用户")]);
        }
      }],
      userData: [],
      totalUser: 0
    };
  },
  methods: {
    changeUserPage: function changeUserPage(v) {
      this.searchUserForm.pageNumber = v;
      this.getUserDataList();
    },
    changeUserPageSize: function changeUserPageSize(v) {
      this.searchUserForm.pageSize = v;
      this.getUserDataList();
    },
    getUserDataList: function getUserDataList() {
      var _this2 = this;

      this.userLoading = true;
      getUserListData(this.searchUserForm).then(function (res) {
        _this2.userLoading = false;

        if (res.success) {
          _this2.data = res.result.records;
          _this2.total = res.result.total;

          if (res.result.content || res.result.totalElements) {
            _this2.data = res.result.content;
            _this2.total = res.result.totalElements;
          }
        }
      });
    },
    handleSearchUser: function handleSearchUser() {
      this.searchUserForm.pageNumber = 1;
      this.searchUserForm.pageSize = 9;
      this.getUserDataList();
    },
    handleResetUser: function handleResetUser() {
      this.$refs.searchUserForm.resetFields();
      this.searchUserForm.pageNumber = 1;
      this.searchUserForm.pageSize = 9;
      this.$refs.dep.clearSelect(); // 重新加载数据

      this.getUserDataList();
    },
    setData: function setData(v) {
      this.selectUsers = v;
      this.$emit("on-change", this.selectUsers);
    },
    chooseUser: function chooseUser(v) {
      // 去重
      var that = this;
      var flag = true;
      this.selectUsers.forEach(function (e) {
        if (v.id == e.id) {
          that.$Message.warning("已经添加过啦，请勿重复选择");
          flag = false;
        }
      });

      if (flag) {
        var u = {
          id: v.id,
          username: v.username
        };
        this.selectUsers.push(u);
        this.$emit("on-change", this.selectUsers);
        this.$Message.success("\u6DFB\u52A0\u7528\u6237 ".concat(v.username, " \u6210\u529F"));
      }
    },
    clearSelectData: function clearSelectData() {
      this.selectUsers = [];
      this.$emit("on-change", this.selectUsers);
    },
    handleCancelUser: function handleCancelUser(e, id) {
      // 删除所选用户
      var newArray = [];
      this.selectUsers.forEach(function (e) {
        if (id != e.id) {
          newArray.push(e);
        }
      });
      this.selectUsers = newArray;
      this.$emit("on-change", this.selectUsers);
      this.$Message.success("删除所选用户成功");
    }
  },
  created: function created() {
    // 计算高度
    this.height = Number(document.documentElement.clientHeight - 230);
    this.getUserDataList();
  }
};
