import { unlock } from "@/api/index";
export default {
  name: "circleLoading",
  data() {
    return {
      loading: false,
      passCheckVisible: false, // 密码验证
      password: ""
    };
  },
  methods: {
    checkPass() {
      this.loading = true;
      unlock({ password: this.password }).then(res => {
        this.loading = false;
        if (res.success) {
          this.passCheckVisible = false;
          this.$emit("on-success", true);
        }
      });
    },
    show(){
      this.password = "";
      this.passCheckVisible = true;
    }
  }
};
